import React, { useState } from 'react';
import './App.css';

interface Prompt {
  text: string;
  type: 'writing' | 'art' | 'music';
  author?: string;
}

interface Encouragement {
  text: string;
  author?: string;
}

const artPrompts: Prompt[] = [
  { text: "Sketch a creature that glows in the dark", type: 'art' },
  { text: "Draw a landscape where the sky and ground have swapped colors", type: 'art' },
  { text: "Design a character who can paint with their mind", type: 'art' },
];

const musicPrompts: Prompt[] = [
  { text: "Compose a melody inspired by the sound of rain on a tin roof", type: 'music' },
  { text: "Create a song that tells the story of a lost constellation", type: 'music' },
  { text: "Compose a piece that combines classical and electronic elements", type: 'music' },
];

const encouragements: Encouragement[] = [
  { text: "Just keep swimming!", author: "Dory" },
  { text: "You're doing great!", author: "Your biggest fan" },
  { text: "Every small step counts!", author: "Your cheerleader" },
  { text: "You've got this!", author: "Your supporter" },
  { text: "Make today amazing!", author: "Your motivator" },
];

function App() {
  const [currentPrompt, setCurrentPrompt] = useState<Prompt | null>(null);
  const [currentEncouragement, setCurrentEncouragement] = useState<Encouragement | null>(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [activeQuadrant, setActiveQuadrant] = useState<'inspiration' | 'image' | 'encouragement' | 'music' | null>(null);

  const fetchWritingPrompt = async () => {
    try {
      const response = await fetch('https://api.quotable.io/random');
      const data = await response.json();
      return {
        text: `Write a story inspired by this quote: "${data.content}"`,
        type: 'writing' as const,
        author: data.author
      };
    } catch (error) {
      console.error('Error fetching quote:', error);
      return {
        text: "Write a story about a library that contains books from the future",
        type: 'writing' as const
      };
    }
  };

  const getRandomPrompt = async (quadrant: 'inspiration' | 'image' | 'encouragement' | 'music') => {
    setIsAnimating(true);
    setIsLoading(true);
    setActiveQuadrant(quadrant);
    
    try {
      let newPrompt: Prompt | Encouragement | null = null;

      switch (quadrant) {
        case 'inspiration':
          newPrompt = await fetchWritingPrompt();
          setCurrentPrompt(newPrompt as Prompt);
          break;
        case 'image':
          // TODO: Implement image generation
          newPrompt = artPrompts[Math.floor(Math.random() * artPrompts.length)];
          setCurrentPrompt(newPrompt as Prompt);
          break;
        case 'encouragement':
          newPrompt = encouragements[Math.floor(Math.random() * encouragements.length)];
          setCurrentEncouragement(newPrompt);
          break;
        case 'music':
          newPrompt = musicPrompts[Math.floor(Math.random() * musicPrompts.length)];
          setCurrentPrompt(newPrompt as Prompt);
          break;
      }

      setTimeout(() => {
        setIsAnimating(false);
        setIsLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Error getting prompt:', error);
      setIsAnimating(false);
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-indigo-800 to-blue-900 flex flex-col items-center justify-center p-4">
      <h1 className="text-4xl font-bold text-white mb-8 text-center">
        Magic Button
      </h1>
      
      <div className="relative w-full max-w-4xl aspect-square">
        {/* Quadrants */}
        <div className="absolute inset-0 grid grid-cols-2 grid-rows-2 gap-4">
          {/* Top Left - Inspiration */}
          <div 
            className="bg-white/5 backdrop-blur-sm rounded-lg p-4 cursor-pointer hover:bg-white/10 transition-all"
            onClick={() => getRandomPrompt('inspiration')}
          >
            <h2 className="text-white text-xl font-semibold mb-2">Inspiration</h2>
            {activeQuadrant === 'inspiration' && currentPrompt && (
              <div className="text-white">
                <span className="inline-block px-3 py-1 rounded-full text-sm font-semibold mb-2 bg-blue-500">
                  Writing
                </span>
                <p className="text-lg">{currentPrompt.text}</p>
                {currentPrompt.author && (
                  <p className="text-sm mt-2 text-gray-300">Quote by: {currentPrompt.author}</p>
                )}
              </div>
            )}
          </div>

          {/* Top Right - Image Generation */}
          <div 
            className="bg-white/5 backdrop-blur-sm rounded-lg p-4 cursor-pointer hover:bg-white/10 transition-all"
            onClick={() => getRandomPrompt('image')}
          >
            <h2 className="text-white text-xl font-semibold mb-2">Art Prompt</h2>
            {activeQuadrant === 'image' && currentPrompt && (
              <div className="text-white">
                <span className="inline-block px-3 py-1 rounded-full text-sm font-semibold mb-2 bg-green-500">
                  Art
                </span>
                <p className="text-lg">{currentPrompt.text}</p>
              </div>
            )}
          </div>

          {/* Bottom Left - Encouragement */}
          <div 
            className="bg-white/5 backdrop-blur-sm rounded-lg p-4 cursor-pointer hover:bg-white/10 transition-all"
            onClick={() => getRandomPrompt('encouragement')}
          >
            <h2 className="text-white text-xl font-semibold mb-2">Encouragement</h2>
            {activeQuadrant === 'encouragement' && currentEncouragement && (
              <div className="text-white">
                <p className="text-lg">{currentEncouragement.text}</p>
                {currentEncouragement.author && (
                  <p className="text-sm mt-2 text-gray-300">- {currentEncouragement.author}</p>
                )}
              </div>
            )}
          </div>

          {/* Bottom Right - Music */}
          <div 
            className="bg-white/5 backdrop-blur-sm rounded-lg p-4 cursor-pointer hover:bg-white/10 transition-all"
            onClick={() => getRandomPrompt('music')}
          >
            <h2 className="text-white text-xl font-semibold mb-2">Music</h2>
            {activeQuadrant === 'music' && currentPrompt && (
              <div className="text-white">
                <span className="inline-block px-3 py-1 rounded-full text-sm font-semibold mb-2 bg-purple-500">
                  Music
                </span>
                <p className="text-lg">{currentPrompt.text}</p>
              </div>
            )}
          </div>
        </div>

        {/* Center Button */}
        <button
          onClick={() => getRandomPrompt('inspiration')}
          disabled={isLoading}
          className={`absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2
            w-32 h-32 rounded-full bg-gradient-to-r from-pink-500 via-purple-500 to-indigo-500 
            transform transition-all duration-300 hover:scale-105 active:scale-95
            ${isAnimating ? 'animate-pulse-slow' : ''}
            shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed
            z-10`}
        >
          <div className="absolute inset-0 rounded-full bg-white opacity-20 animate-pulse"></div>
          <span className="absolute inset-0 flex items-center justify-center text-white text-xl font-bold">
            {isLoading ? 'Loading...' : 'Click Me!'}
          </span>
        </button>
      </div>
    </div>
  );
}

export default App;
